CREATE TABLE jiraissue (  ID decimal(18,0) NOT NULL,  pkey varchar(255) DEFAULT NULL,  PROJECT decimal(18,0) DEFAULT NULL,  REPORTER varchar(255) DEFAULT NULL,  ASSIGNEE varchar(255) DEFAULT NULL,  issuetype varchar(255) DEFAULT NULL,  SUMMARY varchar(255) DEFAULT NULL,  DESCRIPTION LONGVARCHAR,  ENVIRONMENT LONGVARCHAR,  PRIORITY varchar(255) DEFAULT NULL,  RESOLUTION varchar(255) DEFAULT NULL,  issuestatus varchar(255) DEFAULT NULL,  CREATED datetime DEFAULT NULL,  UPDATED datetime DEFAULT NULL,  DUEDATE datetime DEFAULT NULL,  VOTES decimal(18,0) DEFAULT NULL,  TIMEORIGINALESTIMATE decimal(18,0) DEFAULT NULL,  TIMEESTIMATE decimal(18,0) DEFAULT NULL,  TIMESPENT decimal(18,0) DEFAULT NULL,  WORKFLOW_ID decimal(18,0) DEFAULT NULL,  SECURITY decimal(18,0) DEFAULT NULL,  FIXFOR decimal(18,0) DEFAULT NULL,  COMPONENT decimal(18,0) DEFAULT NULL,  RESOLUTIONDATE datetime DEFAULT NULL,  WATCHES decimal(18,0) DEFAULT NULL,  issuenum decimal(18,0) DEFAULT 0, PRIMARY KEY (ID));
