CREATE TABLE jiraissue (  ID decimal(18,0) NOT NULL,  issuenum decimal(18,0) NOT NULL,  PROJECT decimal(18,0) DEFAULT NULL,  REPORTER varchar(255) DEFAULT NULL,  ASSIGNEE varchar(255) DEFAULT NULL,  issuetype varchar(255) DEFAULT NULL,  SUMMARY varchar(255) DEFAULT NULL,  DESCRIPTION LONGVARCHAR,  ENVIRONMENT LONGVARCHAR,  PRIORITY varchar(255) DEFAULT NULL,  RESOLUTION varchar(255) DEFAULT NULL,  issuestatus varchar(255) DEFAULT NULL,  CREATED datetime DEFAULT NULL,  UPDATED datetime DEFAULT NULL,  DUEDATE datetime DEFAULT NULL,  VOTES decimal(18,0) DEFAULT NULL,  TIMEORIGINALESTIMATE decimal(18,0) DEFAULT NULL,  TIMEESTIMATE decimal(18,0) DEFAULT NULL,  TIMESPENT decimal(18,0) DEFAULT NULL,  WORKFLOW_ID decimal(18,0) DEFAULT NULL,  SECURITY decimal(18,0) DEFAULT NULL,  FIXFOR decimal(18,0) DEFAULT NULL,  COMPONENT decimal(18,0) DEFAULT NULL,  RESOLUTIONDATE datetime DEFAULT NULL,  WATCHES decimal(18,0) DEFAULT NULL,  CREATOR varchar(255) DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE component (  ID decimal(18,0) NOT NULL,  PROJECT decimal(18,0) default NULL,  cname varchar(255) default NULL,  description varchar(255),  URL varchar(255) default NULL,  LEAD varchar(255) default NULL,  ASSIGNEETYPE decimal(18,0) default NULL,  PRIMARY KEY  (ID));
CREATE TABLE projectversion (  ID decimal(18,0) NOT NULL,  PROJECT decimal(18,0) default NULL,  vname varchar(255) default NULL,  DESCRIPTION varchar(255),  SEQUENCE decimal(18,0) default NULL,  RELEASED varchar(10) default NULL,  ARCHIVED varchar(10) default NULL,  URL varchar(255) default NULL,  RELEASEDATE datetime default NULL,  PRIMARY KEY  (ID));
CREATE TABLE customfield (  ID decimal(18,0) NOT NULL,  CUSTOMFIELDTYPEKEY varchar(255) default NULL,  CUSTOMFIELDSEARCHERKEY varchar(255) default NULL,  cfname varchar(255) default NULL,  DESCRIPTION varchar(255),  defaultvalue varchar(255) default NULL,  FIELDTYPE decimal(18,0) default NULL,  PROJECT decimal(18,0) default NULL,  ISSUETYPE varchar(255) default NULL,  PRIMARY KEY  (ID));
CREATE TABLE customfieldoption (  ID decimal(18,0) NOT NULL,  CUSTOMFIELD decimal(18,0) default NULL,  CUSTOMFIELDCONFIG decimal(18,0) default NULL,  PARENTOPTIONID decimal(18,0) default NULL,  SEQUENCE decimal(18,0) default NULL,  customvalue varchar(255) default NULL,  optiontype varchar(60) default NULL,  disabled varchar(60) default NULL,  PRIMARY KEY  (ID));
CREATE TABLE sequence_value_item ( SEQ_NAME varchar(60) NOT NULL, SEQ_ID decimal(18,0) DEFAULT NULL, PRIMARY KEY (SEQ_NAME));
CREATE TABLE os_currentstep (  ID decimal(18,0) NOT NULL,  ENTRY_ID decimal(18,0) DEFAULT NULL,  STEP_ID decimal(9,0) DEFAULT NULL,  ACTION_ID decimal(9,0) DEFAULT NULL,  OWNER varchar(60) DEFAULT NULL,  START_DATE datetime DEFAULT NULL,  DUE_DATE datetime DEFAULT NULL,  FINISH_DATE datetime DEFAULT NULL,  STATUS varchar(60) DEFAULT NULL,  CALLER varchar(60) DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE os_wfentry (  ID decimal(18,0) NOT NULL,  NAME varchar(255) DEFAULT NULL,  INITIALIZED decimal(9,0) DEFAULT NULL,  STATE decimal(9,0) DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE changegroup (  ID decimal(18,0) NOT NULL,  issueid decimal(18,0) DEFAULT NULL,  AUTHOR varchar(255) DEFAULT NULL,  CREATED datetime DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE changeitem (  ID decimal(18,0) NOT NULL,  groupid decimal(18,0) DEFAULT NULL,  FIELDTYPE varchar(255) DEFAULT NULL,  FIELD varchar(255) DEFAULT NULL,  OLDVALUE LONGVARCHAR,  OLDSTRING LONGVARCHAR,  NEWVALUE LONGVARCHAR,  NEWSTRING LONGVARCHAR,  PRIMARY KEY (ID));
CREATE TABLE jiraworkflows (  ID decimal(18,0) NOT NULL,  workflowname varchar(255) DEFAULT NULL,  creatorname varchar(255) DEFAULT NULL,  DESCRIPTOR LONGVARCHAR,  ISLOCKED varchar(60) DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE userassociation (  SOURCE_NAME varchar(60) NOT NULL,  SINK_NODE_ID decimal(18,0) NOT NULL,  SINK_NODE_ENTITY varchar(60) NOT NULL,  ASSOCIATION_TYPE varchar(60) NOT NULL,  SEQUENCE decimal(9,0) DEFAULT NULL,  CREATED datetime DEFAULT NULL,  PRIMARY KEY (SOURCE_NAME,SINK_NODE_ID,SINK_NODE_ENTITY,ASSOCIATION_TYPE));
CREATE TABLE customfieldvalue (  ID decimal(18,0) NOT NULL,  ISSUE decimal(18,0) DEFAULT NULL,  CUSTOMFIELD decimal(18,0) DEFAULT NULL,  PARENTKEY varchar(255) DEFAULT NULL,  STRINGVALUE varchar(255) DEFAULT NULL,  NUMBERVALUE decimal(18,6) DEFAULT NULL,  TEXTVALUE LONGVARCHAR,  DATEVALUE datetime DEFAULT NULL,  VALUETYPE varchar(255) DEFAULT NULL,  PRIMARY KEY (ID));
CREATE TABLE label (  ID decimal(18,0) NOT NULL,  FIELDID decimal(18,0) DEFAULT NULL,  ISSUE decimal(18,0) DEFAULT NULL,  LABEL varchar(255) DEFAULT NULL,  PRIMARY KEY (ID));
