\n\ncreate table RF_COEFFICIENTSCORRECTEURS_FE1 (COEFFICIENTSCORRECTEURS integer not null, JOUR_DATE_CALENDRIER DATE not null, primary key (JOUR_DATE_CALENDRIER, COEFFICIENTSCORRECTEURS));\n\ncreate table RF_JOUR_ETABLISSEMENT_EX_F_784 (JOUR_DATE_CALENDRIER TIMESTAMP not null, ETABLISSEMENT_CODE VARCHAR(3) not null, primary key (JOUR_DATE_CALENDRIER, ETABLISSEMENT_CODE));\n\nalors que JOUR_DATE_CALENDRIER devrait être de type DATE;\n',NULL,'3','1','6','2009-03-23 16:23:31','2010-02-04 10:18:24',NULL,'0',NULL,NULL,NULL,'19001',NULL,NULL,NULL,'2009-09-09 11:00:23','0');
